INSERT INTO `wp_aec_event` VALUES ('1', '0', 'Ajax Event Calendar [v1.0.4] successfully installed!', '2014-09-17 00:00:00', '2014-09-17 00:00:00', '0', '0', '2014-09-17', '1', '1', 'Ajax Event Calendar WordPress Plugin is a fully localized (including RTL language support) community calendar that allows authorized users to add, edit, copy, move, resize and delete events into custom categories.  Highly customized calendars can be added to pages, posts or text widgets using the <strong>[calendar]</strong> shortcode.  Similarly, a list view of events can be added using the <strong>[eventlist]</strong> shortcode.  Click on the event link to learn about available the shortcode options.', 'http://wordpress.org/extend/plugins/ajax-event-calendar/', 'Cloud Gate \"The Bean\"', '201 East Randolph Street', 'Chicago', 'Illinois', '60601-6530', 'United States', 'Eran Miller', '123-123-1234', '1', '0');
