INSERT INTO `wp_posts` VALUES ('90', '1', '2014-09-17 18:34:01', '2014-09-17 14:34:01', '<!--:ru-->\r\nБагамские острова — архипелаг в Атлантическом океане, аккурат между Кубой и полуостровом Флорида. Он насчитывает около 2500 рифов и 700 островов, из которых обитаемы только 30. Омываемые тёплыми водами Гольфстрима, Багамские острова давно стали одним из синонимов понятия «роскошный отдых». Это одно из самых богатых государств региона, крупный оффшорный центр (на островах — более 400 банков) и центр игорного бизнеса — казино здесь открыты круглые сутки. Плюс, конечно, не стоит забывать о сотнях километров белоснежных песчаных пляжей, коралловых рифах и прозрачных прибрежных водах, приводящих в восторг любителей дайвинга.\r\n\r\n<!--:-->', '<!--:ru-->Багамские острова<!--:-->', '', 'publish', 'open', 'open', '', 'bagamskie-ostrova', '', '', '2014-09-17 18:34:01', '2014-09-17 14:34:01', '', '0', 'http://bgufk.yandex.od.ua/?p=90', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('92', '1', '2014-09-17 18:34:01', '2014-09-17 14:34:01', '<!--:ru-->\r\nБагамские острова — архипелаг в Атлантическом океане, аккурат между Кубой и полуостровом Флорида. Он насчитывает около 2500 рифов и 700 островов, из которых обитаемы только 30. Омываемые тёплыми водами Гольфстрима, Багамские острова давно стали одним из синонимов понятия «роскошный отдых». Это одно из самых богатых государств региона, крупный оффшорный центр (на островах — более 400 банков) и центр игорного бизнеса — казино здесь открыты круглые сутки. Плюс, конечно, не стоит забывать о сотнях километров белоснежных песчаных пляжей, коралловых рифах и прозрачных прибрежных водах, приводящих в восторг любителей дайвинга.\r\n\r\n<!--:-->', '<!--:ru-->Багамские острова<!--:-->', '', 'inherit', 'open', 'open', '', '90-revision-v1', '', '', '2014-09-17 18:34:01', '2014-09-17 14:34:01', '', '90', 'http://bgufk.yandex.od.ua/90-revision-v1/', '0', 'revision', '', '0');
